import React, { useEffect, useState } from "react";
import axios from "axios";

const GenerateTimeTable = () => {
  const [entries, setEntries] = useState([]);
  const [timetable, setTimetable] = useState(null);
  const accessToken = localStorage.getItem("accessToken");

  useEffect(() => {
    axios.get("http://localhost:8000/api/teachers/", {
      headers: {
        Authorization: `Bearer ${accessToken}`,
      },
    })
      .then(res => {
        const formattedEntries = res.data.map(teacher => ({
          name: teacher.name,
          slot: teacher.slot || "",  // Add slot if your backend provides it
          subjects: teacher.courses.map(course => course.name).join(", "),
          max_hours: teacher.max_hours || "",  // Ensure this matches your backend field
        }));
        setEntries(formattedEntries);
      })
      .catch(err => console.error("Failed to fetch teachers:", err));
  }, []);

  const handleChange = (index, field, value) => {
    const updated = [...entries];
    updated[index][field] = value;
    setEntries(updated);
  };

  const addEntry = () => {
    setEntries([...entries, { name: "", slot: "", subjects: "", max_hours: "" }]);
  };

  const handleGenerate = () => {
    axios.post("http://localhost:8000/api/generate_timetable/", { entries }, {
      headers: {
        Authorization: `Bearer ${accessToken}`,
      },
    })
      .then(res => setTimetable(res.data))
      .catch(err => console.error("Failed to generate timetable:", err));
  };

  return (
    <div className="p-8">
      <h2 className="text-xl font-bold mb-4">Welcome Sakshi!</h2>
      <p className="mb-6">Stay Organised, Stay Ahead</p>

      <div className="space-y-3">
        {entries.map((entry, index) => (
          <div key={index} className="flex space-x-4 items-center">
            <input className="border px-2 py-1" placeholder="Name" value={entry.name} onChange={e => handleChange(index, "name", e.target.value)} />
            <input className="border px-2 py-1" placeholder="Slot" value={entry.slot} onChange={e => handleChange(index, "slot", e.target.value)} />
            <input className="border px-2 py-1" placeholder="Subjects" value={entry.subjects} onChange={e => handleChange(index, "subjects", e.target.value)} />
            <input className="border px-2 py-1" type="number" placeholder="Max Hours" value={entry.max_hours} onChange={e => handleChange(index, "max_hours", e.target.value)} />
          </div>
        ))}
      </div>

      <button onClick={addEntry} className="mt-4 bg-red-600 text-white px-4 py-2 rounded">âž• Add new entry</button>

      <button onClick={handleGenerate} className="mt-4 block bg-red-700 text-white px-6 py-2 rounded shadow-md">Generate Time Table</button>

      {timetable && (
        <div className="mt-6">
          <h3 className="font-semibold text-lg mb-2">Generated Time Table</h3>
          <table className="w-full border border-gray-300">
            <thead>
              <tr>
                {Object.keys(timetable[0]).map((col, i) => (
                  <th key={i} className="border p-2">{col}</th>
                ))}
              </tr>
            </thead>
            <tbody>
              {timetable.map((row, i) => (
                <tr key={i}>
                  {Object.values(row).map((val, j) => (
                    <td key={j} className="border p-2">{val}</td>
                  ))}
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
};

export default GenerateTimeTable;
