import React, { useState } from 'react';
import { Form, Button } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { useAuth } from '../context/AuthContext'; // import Auth context

const LoginForm = () => {
  const navigate = useNavigate();
  const { setUser } = useAuth(); // get setUser from context

  const [formData, setFormData] = useState({
    username: '',
    password: '',
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const { username, password } = formData;

    try {
      // Login and get tokens
      const response = await axios.post('http://localhost:8000/api/login/', {
        username,
        password,
      });

      const access = response.data.access;
      const refresh = response.data.refresh;

      // Store tokens
      localStorage.setItem('accessToken', access);
      localStorage.setItem('refreshToken', refresh);

      // Fetch user info
      const userInfoRes = await axios.get('http://localhost:8000/api/user-info/', {
        headers: {
          Authorization: `Bearer ${access}`,
        },
      });

      const userData = userInfoRes.data;

      // Set user in context
      setUser(userData);

      // Redirect to appropriate dashboard
      if (userData.role === 'teacher') {
        navigate('/teacher-dashboard');
      } else {
        navigate('/student-dashboard');
      }

    } catch (err) {
      console.error('Login failed:', err.response?.data || err.message);
      alert("Invalid credentials or server error.");
    }
  };

  return (
    <div className="p-5 shadow rounded bg-white w-100" style={{ maxWidth: '400px' }}>
      <h2 className="fw-bold mb-1">Welcome!</h2>
      <p className="mb-4 text-muted">to the campus Portal.</p>

      <Form onSubmit={handleSubmit}>
        <Form.Group className="mb-3">
          <Form.Label>Username</Form.Label>
          <Form.Control
            type="text"
            name="username"
            value={formData.username}
            onChange={handleChange}
            placeholder="Enter username"
            required
          />
        </Form.Group>

        <Form.Group className="mb-4">
          <Form.Label>Password</Form.Label>
          <Form.Control
            type="password"
            name="password"
            value={formData.password}
            onChange={handleChange}
            placeholder="Enter password"
            required
          />
        </Form.Group>

        <Button variant="danger" type="submit" className="w-100 fw-bold">
          Login
        </Button>
      </Form>
    </div>
  );
};

export default LoginForm;
