// src/pages/Teachers.jsx

import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { FaEdit, FaTrash } from 'react-icons/fa';

const Teachers = () => {
  const [teachers, setTeachers] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const [newTeacher, setNewTeacher] = useState({ first_name: '', last_name: '', designation: '' });
  const [editId, setEditId] = useState(null);

  const fetchTeachers = async () => {
    const res = await axios.get('http://localhost:8000/teachers/');
    setTeachers(res.data);
  };

  useEffect(() => {
    fetchTeachers();
  }, []);

  const handleChange = (e) => {
    setNewTeacher({ ...newTeacher, [e.target.name]: e.target.value });
  };

  const handleSubmit = async () => {
    if (editId) {
      await axios.put(`http://localhost:8000/teachers/${editId}/`, newTeacher);
      setEditId(null);
    } else {
      await axios.post('http://localhost:8000/teachers/', newTeacher);
    }
    setNewTeacher({ first_name: '', last_name: '', designation: '' });
    setShowForm(false);
    fetchTeachers();
  };

  const handleEdit = (teacher) => {
    setNewTeacher(teacher);
    setEditId(teacher.id);
    setShowForm(true);
  };

  const handleDelete = async (id) => {
    await axios.delete(`http://localhost:8000/teachers/${id}/`);
    fetchTeachers();
  };

  return (
    <div className="p-4">
      <h4 className="fw-bold">Welcome Sakshi !</h4>
      <p className="text-muted">Stay Organised , Stay Ahead</p>

      <h2 className="fw-bold mt-4">Teachers</h2>
      <p className="text-muted">Existing Teachers</p>

      <table className="table table-borderless">
        <thead className="fw-bold">
          <tr>
            <th>First Name</th>
            <th>Last Name</th>
            <th>Designation</th>
            <th></th>
          </tr>
        </thead>
        <tbody>
          {teachers.map((t) => (
            <tr key={t.id}>
              <td>{t.first_name}</td>
              <td>{t.last_name}</td>
              <td>{t.designation}</td>
              <td>
                <button className="btn btn-sm btn-outline-secondary me-2" onClick={() => handleEdit(t)}>
                  <FaEdit />
                </button>
                <button className="btn btn-sm btn-outline-secondary" onClick={() => handleDelete(t.id)}>
                  <FaTrash />
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      <button
        className="btn btn-sm btn-light text-danger border border-danger mt-3"
        onClick={() => {
          setShowForm(!showForm);
          setNewTeacher({ first_name: '', last_name: '', designation: '' });
          setEditId(null);
        }}
      >
        {editId ? 'Cancel Editing' : 'Add New Entry'}
      </button>

      {showForm && (
        <div className="row g-2 mt-3">
          <div className="col-md-3">
            <input
              type="text"
              name="first_name"
              className="form-control"
              placeholder="First Name"
              value={newTeacher.first_name}
              onChange={handleChange}
            />
          </div>
          <div className="col-md-3">
            <input
              type="text"
              name="last_name"
              className="form-control"
              placeholder="Last Name"
              value={newTeacher.last_name}
              onChange={handleChange}
            />
          </div>
          <div className="col-md-3">
            <input
              type="text"
              name="designation"
              className="form-control"
              placeholder="Designation"
              value={newTeacher.designation}
              onChange={handleChange}
            />
          </div>
          <div className="col-md-3">
            <button className="btn btn-danger w-100" onClick={handleSubmit}>
              {editId ? 'Update' : 'Add'}
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default Teachers;
