// src/pages/Login.jsx
import React from 'react';
import LoginForm from '../components/LoginForm';  // Import the LoginForm component

const Login = () => {
  return (
    <div className="d-flex" style={{ height: '100vh' }}>
      <div className="w-50 bg-light d-flex align-items-center justify-content-center">
        <img src="https://via.placeholder.com/600x400" alt="Campus" className="img-fluid" />
      </div>
      <div className="w-50 d-flex align-items-center justify-content-center bg-light">
        <div className="text-center" style={{ width: '300px' }}>
          <h2 className="fw-bold">Welcome!</h2>
          <p className="mb-4">to the campus Portal.</p>
          <LoginForm />  {/* Render the LoginForm component */}
        </div>
      </div>
    </div>
  );
};

export default Login;
