import React, { useContext } from "react";
import { useAuth } from '../context/AuthContext'; // ✅ Correct


const WelcomeCards = () => {
  const { user } = useAuth();

  const studentCards = [
    { title: "Your Timetable", button: "View Timetable" },
    { title: "Find Your Teacher", button: "Search Availability" },
    { title: "Lab Locator", button: "Find Lab" },
  ];

  const teacherCards = [
    { title: "Your Timetable", button: "View Timetable" },
    { title: "Faculty Availability", button: "Check Availability" },
    { title: "Classroom Availability", button: "Search Rooms" },
  ];

  const cards = user.role === "teacher" ? teacherCards : studentCards;

  return (
    <div className="cards">
      {cards.map((card, idx) => (
        <div className="card" key={idx}>
          <h3>{card.title}</h3>
          <button>{card.button}</button>
        </div>
      ))}
    </div>
  );
};

export default WelcomeCards;
