import React, { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const GenerateTimeTable = () => {
  const [teachers, setTeachers] = useState([]);
  const [courses, setCourses] = useState([]);
  const [timeslots, setTimeslots] = useState([]);
  const [timetable, setTimetable] = useState(null);
  const [maxHours, setMaxHours] = useState(5);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  const accessToken = localStorage.getItem("accessToken");
  const navigate = useNavigate();

  useEffect(() => {
    if (!accessToken) {
      setError("No access token found!");
      navigate("/login");
      return;
    }

    const fetchData = async () => {
      setLoading(true);
      try {
        const [teachersRes, coursesRes, timeslotsRes] = await Promise.all([
          axios.get("http://localhost:8000/api/teachers/", {
            headers: { Authorization: `Bearer ${accessToken}` },
          }),
          axios.get("http://localhost:8000/api/courses/", {
            headers: { Authorization: `Bearer ${accessToken}` },
          }),
          axios.get("http://localhost:8000/api/timeslots/", {
            headers: { Authorization: `Bearer ${accessToken}` },
          }),
        ]);

        setTeachers(teachersRes.data);
        setCourses(coursesRes.data);
        setTimeslots(timeslotsRes.data);
      } catch (err) {
        console.error("Failed to fetch data:", err);
        if (err.response?.status === 401) {
          setError("Unauthorized: Please log in again.");
          navigate("/login");
        } else {
          setError("An error occurred while fetching data.");
        }
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, [accessToken, navigate]);

  const handleGenerate = () => {
    setLoading(true);
    axios
      .post(
        "http://localhost:8000/api/generate_timetable/",
        { max_hours_per_day: maxHours },
        { headers: { Authorization: `Bearer ${accessToken}` } }
      )
      .then((res) => {
        setTimetable(res.data);
        setError("");
      })
      .catch((err) => {
        console.error("Failed to generate timetable", err);
        setError("Failed to generate timetable. Please try again.");
      })
      .finally(() => setLoading(false));
  };

  if (loading) return <div>Loading...</div>;

  return (
    <div className="p-8 bg-[#fdfde0] min-h-screen">
      <h2 className="text-2xl font-bold mb-2">Welcome Sakshi!</h2>
      <p className="mb-6 text-gray-700">Stay Organized, Stay Ahead</p>

      {error && <div className="text-red-500 mb-4">{error}</div>}

      <div className="mb-6">
        <label className="block mb-2 font-medium">Max Hours Per Day:</label>
        <input
          type="number"
          value={maxHours}
          onChange={(e) => setMaxHours(parseInt(e.target.value))}
          className="border px-3 py-2 rounded w-32"
          min="1"
          max="12"
        />
      </div>

      <button
        onClick={handleGenerate}
        className="bg-red-700 text-white px-6 py-2 rounded shadow-md"
      >
        Generate Timetable
      </button>

      {/* Display fetched data */}
      <div className="mt-10">
        <h3 className="text-xl font-semibold mb-2">Teachers</h3>
        <ul className="list-disc pl-6 mb-6">
          {teachers.map((t, i) => (
            <li key={i}>
              {t.first_name} {t.last_name} - {t.courses.map(c => c.name).join(", ")}
            </li>
          ))}
        </ul>

        <h3 className="text-xl font-semibold mb-2">Courses</h3>
        <ul className="list-disc pl-6 mb-6">
          {courses.map((c, i) => (
            <li key={i}>{c.name}</li>
          ))}
        </ul>

        <h3 className="text-xl font-semibold mb-2">Available Time Slots</h3>
        <ul className="list-disc pl-6 mb-6">
          {timeslots.map((slot, i) => (
            <li key={i}>{slot}</li>
          ))}
        </ul>
      </div>

      {/* Display generated timetable */}
      {timetable && (
        <div className="mt-10">
          <h3 className="font-semibold text-lg mb-2">Generated Time Table</h3>
          <table className="w-full border border-gray-300">
            <thead>
              <tr>
                {Object.keys(timetable[0]).map((col, i) => (
                  <th key={i} className="border p-2 bg-gray-100">
                    {col}
                  </th>
                ))}
              </tr>
            </thead>
            <tbody>
              {timetable.map((row, i) => (
                <tr key={i}>
                  {Object.values(row).map((val, j) => (
                    <td key={j} className="border p-2">
                      {typeof val === "object" && val !== null
                        ? JSON.stringify(val)
                        : val}
                    </td>
                  ))}
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
};

export default GenerateTimeTable;
