import React from 'react';
import { useAuth } from '../context/AuthContext';

const Sidebar = () => {
  const { user } = useAuth();

  return (
    <div className="bg-danger text-white p-3 vh-100 d-flex flex-column gap-3">
      <div className="text-center">
        <img src="https://via.placeholder.com/100" alt="Profile" className="rounded-circle mb-2" />
        <h5>{user?.name}</h5>
        <p>{user?.id}</p>
      </div>
      <nav className="d-flex flex-column gap-2">
        <a href="#" className="text-white">Dashboard</a>
        <a href="#" className="text-white">About</a>
        <a href="#" className="text-white">Profile Setting</a>
        <a href="#" className="text-white">Change Pin</a>
        <a href="#" className="text-white">Logout</a>
      </nav>
    </div>
  );
};
export default Sidebar;
