import React, { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const GenerateTimeTable = () => {
  const [teachers, setTeachers] = useState([]);
  const [courses, setCourses] = useState([]);
  const [timeslots, setTimeslots] = useState([]);
  const [entries, setEntries] = useState([]);

  const [selectedTeacher, setSelectedTeacher] = useState("");
  const [selectedTimeSlot, setSelectedTimeSlot] = useState("");
  const [selectedCourses, setSelectedCourses] = useState([]);
  const [maxHours, setMaxHours] = useState(6);

  const [timetable, setTimetable] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  const accessToken = localStorage.getItem("accessToken");
  const navigate = useNavigate();

  useEffect(() => {
    if (!accessToken) {
      setError("No access token found!");
      navigate("/login");
      return;
    }

    const fetchData = async () => {
      try {
        const [teachersRes, coursesRes, timeslotRes] = await Promise.all([
          axios.get("http://localhost:8000/api/teachers/", {
            headers: { Authorization: `Bearer ${accessToken}` },
          }),
          axios.get("http://localhost:8000/api/courses/", {
            headers: { Authorization: `Bearer ${accessToken}` },
          }),
          axios.get("http://localhost:8000/api/timeslots/", {
            headers: { Authorization: `Bearer ${accessToken}` },
          }),
        ]);

        setTeachers(teachersRes.data);
        setCourses(coursesRes.data);
        setTimeslots(timeslotRes.data);
      } catch (err) {
        setError("Failed to fetch data.");
      }
    };

    fetchData();
  }, [accessToken, navigate]);

  const addEntry = () => {
    if (!selectedTeacher || !selectedTimeSlot || selectedCourses.length === 0) {
      alert("Please fill all fields.");
      return;
    }

    setEntries([
      ...entries,
      {
        teacher: selectedTeacher,
        time_slot: selectedTimeSlot,
        courses: selectedCourses,
        max_hours: maxHours,
      },
    ]);

    // Reset selections
    setSelectedTeacher("");
    setSelectedTimeSlot("");
    setSelectedCourses([]);
    setMaxHours(6);
  };

  const handleGenerate = async () => {
    setLoading(true);
    try {
      const res = await axios.post(
        "http://localhost:8000/api/generate-timetable/",
        { entries },
        {
          headers: { Authorization: `Bearer ${accessToken}` },
        }
      );
      setTimetable(res.data);
    } catch (err) {
      setError("Failed to generate timetable.");
    } finally {
      setLoading(false);
    }
  };

  if (loading) return <div className="text-center my-5">Loading...</div>;

  return (
    <div className="d-flex">
      {/* Sidebar */}
      <div className="bg-danger text-white p-4" style={{ width: "250px", minHeight: "100vh" }}>
        <img src="/defaulticon.png" className="rounded-circle mb-2" alt="profile" />
        <h5>admin</h5>
        <ul className="list-unstyled">
          <li><a href="#" className="text-white">Dashboard</a></li>
          <li><a href="#" className="text-white">About</a></li>
          <li><a href="#" className="text-white">Profile Setting</a></li>
          <li><a href="#" className="text-white">Change Pin</a></li>
          <li><button onClick={() => navigate("/")} className="btn btn-link text-white">Logout</button></li>
        </ul>
      </div>

      {/* Main Content */}
      <div className="flex-grow-1 p-5" style={{ backgroundColor: "#fdfde0" }}>
        <h4>Welcome Sakshi!</h4>
        <p className="text-muted">Stay Organised , Stay Ahead</p>

        <div className="mb-3">
          <label className="form-label">Select Teacher:</label>
          <select className="form-control" value={selectedTeacher} onChange={(e) => setSelectedTeacher(e.target.value)}>
            <option value="">-- Select --</option>
            {teachers.map((t) => (
              <option key={t.id} value={t.id}>{t.first_name} {t.last_name}</option>
            ))}
          </select>
        </div>

        <div className="mb-3">
          <label className="form-label">Select Time Slot:</label>
          <select className="form-control" value={selectedTimeSlot} onChange={(e) => setSelectedTimeSlot(e.target.value)}>
            <option value="">-- Select --</option>
            {timeslots.map((ts) => (
              <option key={ts.id} value={ts.id}>{ts.start_time} - {ts.end_time}</option>
            ))}
          </select>
        </div>

        <div className="mb-3">
          <label className="form-label">Select Courses:</label>
          <select
            multiple
            className="form-control"
            value={selectedCourses}
            onChange={(e) => setSelectedCourses(Array.from(e.target.selectedOptions, (option) => option.value))}
          >
            {courses.map((c) => (
              <option key={c.id} value={c.id}>{c.name}</option>
            ))}
          </select>
        </div>

        <div className="mb-3">
          <label className="form-label">Max Hours/Day:</label>
          <input
            type="number"
            className="form-control"
            value={maxHours}
            onChange={(e) => setMaxHours(parseInt(e.target.value))}
            min="1"
            max="12"
          />
        </div>

        <button className="btn btn-danger mb-4" onClick={addEntry}>
          + Add new entry
        </button>

        {/* Entry List Table */}
        {entries.length > 0 && (
          <table className="table table-bordered table-light">
            <thead>
              <tr>
                <th>Teacher</th>
                <th>Time Slot</th>
                <th>Courses</th>
                <th>Max Hours</th>
              </tr>
            </thead>
            <tbody>
              {entries.map((entry, idx) => (
                <tr key={idx}>
                  <td>{teachers.find(t => t.id === entry.teacher)?.first_name}</td>
                  <td>{timeslots.find(ts => ts.id === entry.time_slot)?.start_time} - {timeslots.find(ts => ts.id === entry.time_slot)?.end_time}</td>
                  <td>{entry.courses.map(cid => courses.find(c => c.id === cid)?.name).join(", ")}</td>
                  <td>{entry.max_hours}</td>
                </tr>
              ))}
            </tbody>
          </table>
        )}

        <button className="btn btn-dark mt-4" onClick={handleGenerate}>Generate Time Table</button>

        {/* Display Timetable */}
        {timetable && (
          <div className="mt-5">
            <h3>Generated Timetable</h3>
            <pre>{JSON.stringify(timetable, null, 2)}</pre>
          </div>
        )}
      </div>
    </div>
  );
};

export default GenerateTimeTable;
