import React from 'react';
import { useAuth } from '../context/AuthContext';
import { Card, Button } from 'react-bootstrap';

const WelcomeCards = () => {
  const { user } = useAuth();

  const cardData = [
    {
      title: 'View Profile',
      text: 'Check and update your profile details.',
      link: '/profile',
      icon: 'bi-person-circle'
    },
    {
      title: 'Courses',
      text: 'Browse or manage your courses.',
      link: '/courses',
      icon: 'bi-journal-bookmark-fill'
    },
    {
      title: 'Announcements',
      text: 'Stay up to date with the latest news.',
      link: '/announcements',
      icon: 'bi-megaphone-fill'
    }
  ];

  return (
    <div className="container mt-4">
      <h3 className="mb-4">Welcome, {user?.first_name || user?.username}!</h3>
      <div className="row">
        {cardData.map((card, idx) => (
          <div className="col-md-4 mb-4" key={idx}>
            <Card className="h-100 shadow-sm">
              <Card.Body>
                <Card.Title>
                  <i className={`bi ${card.icon} me-2`}></i>
                  {card.title}
                </Card.Title>
                <Card.Text>{card.text}</Card.Text>
                <Button href={card.link} variant="primary">Go</Button>
              </Card.Body>
            </Card>
          </div>
        ))}
      </div>
    </div>
  );
};

export default WelcomeCards;
