import React, { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const GenerateTimeTable = () => {
    const [teachers, setTeachers] = useState([]);
    const [courses, setCourses] = useState([]);
    const [rooms, setRooms] = useState([]);
    const [timeslots, setTimeslots] = useState([]);
    const [maxHours, setMaxHours] = useState(5);
    const [timetable, setTimetable] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState("");
  
    const accessToken = localStorage.getItem("accessToken");
    const navigate = useNavigate();
  
    useEffect(() => {
      if (!accessToken) {
        setError("No access token found!");
        navigate("/login");
        return;
      }
  
      const fetchData = async () => {
        try {
          const [teachersRes, coursesRes, roomsRes, timeslotsRes] = await Promise.all([
            axios.get("http://localhost:8000/api/teachers/", {
              headers: { Authorization: `Bearer ${accessToken}` }
            }),
            axios.get("http://localhost:8000/api/courses/", {
              headers: { Authorization: `Bearer ${accessToken}` }
            }),
            axios.get("http://localhost:8000/api/rooms/", {
              headers: { Authorization: `Bearer ${accessToken}` }
            }),
            axios.get("http://localhost:8000/api/timeslots/", {
              headers: { Authorization: `Bearer ${accessToken}` }
            })
          ]);
  
          setTeachers(teachersRes.data);
          setCourses(coursesRes.data);
          setRooms(roomsRes.data);
          setTimeslots(timeslotsRes.data);
        } catch (err) {
          console.error("Failed to fetch data:", err);
          setError(err.response?.status === 401 
            ? "Unauthorized: Please log in again." 
            : "An error occurred while fetching data.");
          if (err.response?.status === 401) navigate("/login");
        } finally {
          setLoading(false);
        }
      };
  
      fetchData();
    }, [accessToken, navigate]);
  
    const handleGenerate = () => {
      setLoading(true);
      axios
        .post(
          "http://localhost:8000/api/generate-timetable/",
          { max_hours_per_day: maxHours },
          { headers: { Authorization: `Bearer ${accessToken}` } }
        )
        .then((res) => {
          setTimetable(res.data);
        })
        .catch((err) => {
          console.error("Failed to generate timetable", err);
          setError("Failed to generate timetable. Please try again.");
        })
        .finally(() => setLoading(false));
    };
  
    if (loading) return <div>Loading...</div>;
  
    return (
      <div className="p-8 bg-[#fdfde0] min-h-screen">
        <h2 className="text-2xl font-bold mb-2">Generate Timetable</h2>
        
        <div className="mb-4">
          <label className="block mb-2">Max Hours Per Day:</label>
          <input
            type="number"
            value={maxHours}
            onChange={(e) => setMaxHours(parseInt(e.target.value))}
            className="border px-2 py-1 rounded"
            min="1"
            max="12"
          />
        </div>
  
        <button 
          onClick={handleGenerate} 
          className="bg-red-700 text-white px-6 py-2 rounded shadow-md"
        >
          Generate Timetable
        </button>
  
        {error && <div className="text-red-500 mt-4">{error}</div>}
  
        {timetable && (
          <div className="mt-6">
            <h3 className="font-semibold text-lg mb-2">Generated Timetable</h3>
            <table className="w-full border border-gray-300">
              <thead>
                <tr>
                  <th className="border p-2 bg-gray-100">Time Slot</th>
                  <th className="border p-2 bg-gray-100">Monday</th>
                  <th className="border p-2 bg-gray-100">Tuesday</th>
                  <th className="border p-2 bg-gray-100">Wednesday</th>
                  <th className="border p-2 bg-gray-100">Thursday</th>
                  <th className="border p-2 bg-gray-100">Friday</th>
                </tr>
              </thead>
              <tbody>
                {Object.entries(timetable).map(([time, days]) => (
                  <tr key={time}>
                    <td className="border p-2">{time}</td>
                    {['Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday'].map(day => (
                      <td key={day} className="border p-2">
                        {days[day] ? `${days[day].course} (${days[day].teacher})` : '-'}
                      </td>
                    ))}
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}
      </div>
    );
  };

export default GenerateTimeTable;
