import React, { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const GenerateTimeTable = () => {
  const [entries, setEntries] = useState([]);
  const [teachersList, setTeachersList] = useState([]);
  const [coursesList, setCoursesList] = useState([]);
  const [timeSlotOptions, setTimeSlotOptions] = useState([]);
  const [timetable, setTimetable] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  const accessToken = localStorage.getItem("accessToken");
  const navigate = useNavigate();

  useEffect(() => {
    if (!accessToken) {
      setError("No access token found!");
      return;
    }

    const fetchData = async () => {
      setLoading(true);
      try {
        // Fetch teachers
        const teacherRes = await axios.get("http://localhost:8000/api/teachers/", {
            headers: { Authorization: `Bearer ${accessToken}` },
          });
          

        const formattedTeachers = teacherRes.data.map((t) => {
            const name = `${t.first_name} ${t.last_name}`;

          return {
            name: name,
            slot: t.slot || "",
            subjects: (t.courses || []).map((c) => c.name).join(", "),
            max_hours: t.max_hours || 6,
          };
        });

        setTeachersList(formattedTeachers.map((t) => t.name));
        setEntries(formattedTeachers);

        // Fetch courses
        const coursesRes = await axios.get("http://localhost:8000/api/courses/", {
            headers: { Authorization: `Bearer ${accessToken}` },
        });
        setCoursesList(coursesRes.data.map((c) => c.name ?? ""));

        // Fetch time slots
        const timeSlotsRes = await axios.get("http://localhost:8000/api/timeslots/", {
            headers: { Authorization: `Bearer ${accessToken}` },

        });
        setTimeSlotOptions(timeSlotsRes.data.map((slot) => slot ?? ""));
      } catch (err) {
        console.error("Failed to fetch data:", err);
        if (err.response?.status === 401) {
          setError("Unauthorized: Please log in again.");
          navigate("/login");
        } else {
          setError("An error occurred while fetching data.");
        }
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, [accessToken, navigate]);

  const handleChange = (index, field, value) => {
    const updated = [...entries];
    updated[index][field] = value;
    setEntries(updated);
  };

  const addEntry = () => {
    setEntries([...entries, { name: "", slot: "", subjects: "", max_hours: "" }]);
  };

  const handleGenerate = () => {
    setLoading(true);
    axios
      .post("http://localhost:8000/api/generate_timetable/", { entries }, {
        headers: { Authorization: `Bearer ${accessToken}` },

      })
      .then((res) => setTimetable(res.data))
      .catch((err) => {
        console.error("Failed to generate timetable", err);
        setError("Failed to generate timetable. Please try again.");
      })
      .finally(() => setLoading(false));
  };

  if (loading) return <div>Loading...</div>;

  return (
    <div className="p-8 bg-[#fdfde0] min-h-screen">
      <h2 className="text-2xl font-bold mb-2">Welcome Sakshi!</h2>
      <p className="mb-6 text-gray-700">Stay Organized, Stay Ahead</p>

      {error && <div className="text-red-500">{error}</div>}

      <div className="space-y-3">
        {entries.map((entry, index) => (
          <div key={index} className="flex space-x-4 items-center bg-white p-3 rounded shadow">
            {/* Teacher */}
            <select
              className="border px-2 py-1 rounded"
              value={entry.name}
              onChange={(e) => handleChange(index, "name", e.target.value)}
            >
              <option value="">Select Teacher</option>
              {teachersList.map((teacher, i) => (
                <option key={i} value={teacher ?? ""}>
                  {teacher ?? "Unknown"}
                </option>
              ))}
            </select>

            {/* Slot */}
            <select
              className="border px-2 py-1 rounded"
              value={entry.slot}
              onChange={(e) => handleChange(index, "slot", e.target.value)}
            >
              <option value="">Select Time Slot</option>
              {timeSlotOptions.map((slot, i) => (
                <option key={i} value={slot ?? ""}>
                  {slot ?? "Unknown"}
                </option>
              ))}
            </select>

            {/* Subject */}
            <select
              className="border px-2 py-1 rounded"
              value={entry.subjects}
              onChange={(e) => handleChange(index, "subjects", e.target.value)}
            >
              <option value="">Select Course</option>
              {coursesList.map((course, i) => (
                <option key={i} value={course ?? ""}>
                  {course ?? "Unknown"}
                </option>
              ))}
            </select>

            {/* Max Hours */}
            <input
              className="border px-2 py-1 rounded w-20"
              type="number"
              placeholder="Max Hours"
              value={entry.max_hours}
              onChange={(e) => handleChange(index, "max_hours", e.target.value)}
            />
          </div>
        ))}
      </div>

      <button onClick={addEntry} className="mt-4 bg-red-600 text-white px-4 py-2 rounded flex items-center">
        ➕ Add new entry
      </button>

      <button onClick={handleGenerate} className="mt-6 bg-red-700 text-white px-6 py-2 rounded shadow-md">
        Generate Time Table
      </button>

      {timetable && (
        <div className="mt-6">
          <h3 className="font-semibold text-lg mb-2">Generated Time Table</h3>
          <table className="w-full border border-gray-300">
            <thead>
              <tr>
                {Object.keys(timetable[0]).map((col, i) => (
                  <th key={i} className="border p-2 bg-gray-100">{col}</th>
                ))}
              </tr>
            </thead>
            <tbody>
              {timetable.map((row, i) => (
                <tr key={i}>
                  {Object.values(row).map((val, j) => (
                    <td key={j} className="border p-2">{val}</td>
                  ))}
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
};

export default GenerateTimeTable;
