import React from 'react';

const WelcomeCards = () => {
  const cards = [
    {
      title: 'Your Timetable',
      text: 'Check your daily and weekly schedule based on your subgroup',
      button: 'View Timetable',
    },
    {
      title: 'Find Your Teacher',
      text: 'Need to meet a teacher? Check their free slots and cabin location',
      button: 'Search Availability',
    },
    {
      title: 'Lab Locator',
      text: 'Need to meet a teacher? Check their free slots and cabin location',
      button: 'Find Lab',
    },
  ];

  return (
    <div className="d-flex gap-4">
      {cards.map((card, idx) => (
        <div key={idx} className="card bg-danger text-white p-3" style={{ width: '18rem' }}>
          <div className="card-body">
            <h5 className="card-title">{card.title}</h5>
            <p className="card-text">{card.text}</p>
            <button className="btn btn-light">{card.button}</button>
          </div>
        </div>
      ))}
    </div>
  );
};

export default WelcomeCards;
