import React, { useEffect, useState } from 'react';
import axios from 'axios';
import Sidebar from '../components/Sidebar';
import { FaEdit, FaTrash } from 'react-icons/fa';

const Courses = () => {
  const [courses, setCourses] = useState([]);
  const [showModal, setShowModal] = useState(false);
  const [editingCourse, setEditingCourse] = useState(null);
  const [formData, setFormData] = useState({
    name: '',
    code: '',
    semester: '',
    department: ''
  });

  useEffect(() => {
    fetchCourses();
  }, []);

  const fetchCourses = () => {
    axios.get('http://localhost:8000/api/courses/')
      .then(response => setCourses(response.data))
      .catch(error => console.error('Error fetching courses:', error));
  };

  const handleInputChange = e => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handleAddClick = () => {
    setEditingCourse(null);
    setFormData({ name: '', code: '', semester: '', department: '' });
    setShowModal(true);
  };

  const handleEditClick = course => {
    setEditingCourse(course.id);
    setFormData(course);
    setShowModal(true);
  };

  const handleDelete = id => {
    axios.delete(`http://localhost:8000/api/courses/${id}/`)
      .then(() => fetchCourses())
      .catch(error => console.error('Error deleting course:', error));
  };

  const handleSubmit = e => {
    e.preventDefault();
    if (editingCourse) {
      axios.put(`http://localhost:8000/api/courses/${editingCourse}/`, formData)
        .then(() => {
          fetchCourses();
          setShowModal(false);
        })
        .catch(error => console.error('Error updating course:', error));
    } else {
      axios.post('http://localhost:8000/api/courses/', formData)
        .then(() => {
          fetchCourses();
          setShowModal(false);
        })
        .catch(error => console.error('Error adding course:', error));
    }
  };

  return (
    <div className="d-flex">
      <Sidebar />
      <div className="container mt-4">
        <h2 className="mb-4">Courses</h2>
        <p>Existing Courses</p>
        <table className="table table-striped">
          <thead>
            <tr>
              <th>Course Name</th>
              <th>Course Code</th>
              <th>Semester</th>
              <th>Department</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {courses.map(course => (
              <tr key={course.id}>
                <td>{course.name}</td>
                <td>{course.code}</td>
                <td>{course.semester}</td>
                <td>{course.department}</td>
                <td>
                  <button className="btn btn-sm btn-outline-secondary me-2" onClick={() => handleEditClick(course)}>
                    <FaEdit />
                  </button>
                  <button className="btn btn-sm btn-outline-danger" onClick={() => handleDelete(course.id)}>
                    <FaTrash />
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>

        <button className="btn btn-danger mt-3" onClick={handleAddClick}>Add Course</button>

        {/* Modal */}
        {showModal && (
          <div className="modal d-block" tabIndex="-1" style={{ background: 'rgba(0,0,0,0.5)' }}>
            <div className="modal-dialog">
              <div className="modal-content">
                <form onSubmit={handleSubmit}>
                  <div className="modal-header">
                    <h5 className="modal-title">{editingCourse ? 'Edit Course' : 'Add Course'}</h5>
                    <button type="button" className="btn-close" onClick={() => setShowModal(false)}></button>
                  </div>
                  <div className="modal-body">
                    <div className="mb-3">
                      <label className="form-label">Course Name</label>
                      <input type="text" className="form-control" name="name" value={formData.name} onChange={handleInputChange} required />
                    </div>
                    <div className="mb-3">
                      <label className="form-label">Course Code</label>
                      <input type="text" className="form-control" name="code" value={formData.code} onChange={handleInputChange} required />
                    </div>
                    <div className="mb-3">
                      <label className="form-label">Semester</label>
                      <input type="number" className="form-control" name="semester" value={formData.semester} onChange={handleInputChange} required />
                    </div>
                    <div className="mb-3">
                      <label className="form-label">Department</label>
                      <input type="text" className="form-control" name="department" value={formData.department} onChange={handleInputChange} required />
                    </div>
                  </div>
                  <div className="modal-footer">
                    <button type="submit" className="btn btn-primary">{editingCourse ? 'Update' : 'Add'}</button>
                    <button type="button" className="btn btn-secondary" onClick={() => setShowModal(false)}>Cancel</button>
                  </div>
                </form>
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default Courses;
